import React, { useEffect, useState } from 'react';
import styles from './carousel.module.css'
import Card from './card';
import img from '../../assets/images/carousel/img2.jpg'

const data = [
    {
        id: 1,
        img: img,
        nameCar: "Subaru LEVORG VN5",
        mileage: "2021 • Гибрит • 10 000 км",
        price: "2 115 600 ₽",
        active: true
    },
    {
        id: 2,
        img: img,
        nameCar: "Subaru LEVORG VN5",
        mileage: "2022 • Гибрит • 10 000 км",
        price: "2 115 600 ₽",
        active: false
    },
    {
        id: 3,
        img: img,
        nameCar: "Subaru LEVORG VN5",
        mileage: "2023 • Гибрит • 10 000 км",
        price: "2 115 600 ₽",
        active: false
    },
    {
        id: 4,
        img: img,
        nameCar: "Subaru LEVORG VN5",
        mileage: "2024 • Гибрит • 10 000 км",
        price: "2 115 600 ₽",
        active: false
    },
    // {
    //     id: 5,
    //     img: img,
    //     nameCar: "Subaru LEVORG VN5",
    //     mileage: "2025 • Гибрит • 10 000 км",
    //     price: "2 115 600 ₽",
    //     active: false
    // },
    // {
    //     id: 6,
    //     img: img,
    //     nameCar: "Subaru LEVORG VN5",
    //     mileage: "2026 • Гибрит • 10 000 км",
    //     price: "2 115 600 ₽",
    //     active: false
    // }
]

const Carousel = () => {
    const [index, setIndex] = useState(1);
    const length = data.length;
    // let cards = data.map((card) => {
    //     <Card id={card.id} img={card.img} nameCar={card.nameCar} mileage={card.mileage} price={card.price}/>
    // })
    const handlePrevious = () => {
        const newIndex = index - 1;
        setIndex(newIndex < 0 ? length - 1 : newIndex);
    };

    const handleNext = () => {
        const newIndex = index + 1;
        setIndex(newIndex >= length ? 0 : newIndex);
    };
    useEffect(() => {
            data.map((e) => {e.active = false})
        }, [index])
    data[index].active = true;
    return (
        <div className={styles.carousel}>
            <h1>{index}</h1>
            <div className={styles.carouselBtns}>
                <button className={styles.btnPrev} onClick={handlePrevious}>Previous</button>
                <button className={styles.btnNext} onClick={handleNext}>Next</button>
            </div>
            <div className={styles.carouselItems}>
                <Card id={data[0].id} img={data[0].img} nameCar={data[0].nameCar} mileage={data[0].mileage} price={data[0].price} active={data[0].active}/>
                <Card id={data[1].id} img={data[1].img} nameCar={data[1].nameCar} mileage={data[1].mileage} price={data[1].price} active={data[1].active}/>
                <Card id={data[2].id} img={data[2].img} nameCar={data[2].nameCar} mileage={data[2].mileage} price={data[2].price} active={data[2].active}/>
                <Card id={data[3].id} img={data[3].img} nameCar={data[3].nameCar} mileage={data[3].mileage} price={data[3].price} active={data[3].active}/>
            </div>
        </div>
    );
};


export default Carousel;
